/*
Nome: Luciano Soares Nascimento
Matricula: 11811ECP024
*/

import React from 'react'
import notas from './alunos'


export default props =>
{
    function calcula_media(nota1, nota2, nota3, nota4)
    {
        let media = (nota1 + nota2 + nota3 + nota4) / 4
        if  (media >= 60)
        {
            return "aprovado"
        }
        else
        {
            return "reprovado"
        }

    }

    const lista_todos = notas.map(nota => 
        {
            return (
                <ul>
                    <li>
                        <strong>nome:</strong> {nota.nome} <br/>
                        <strong>nota 1:</strong> {nota.nota_1} <br/>
                        <strong>nota 2:</strong> {nota.nota_2} <br/>
                        <strong>nota 3:</strong> {nota.nota_3} <br/>
                        <strong>nota 4:</strong> {nota.nota_4} <br/>
                        <strong>status: </strong> { calcula_media(nota.nota_1, nota.nota_2, nota.nota_3, nota.nota_4)}
                    </li>
                </ul>
            )
        })

    return (
        <div>
            <ul>
                {lista_todos}
            </ul>
        </div>
    )
}
