import React, { useState } from 'react'
import Conteudo from './Conteudo'
import Main2 from './main2'

import './styles.css'

export default class TeacherScreen extends React.Component
{
    constructor(props){
        super(props)
    }

    state = {
        nota: 0,
        falta: 0,
        id_professor: 2,
        id_aluno: 0,
        tabela: [],
        classes: [],
        classe_selecionada: 0
    };

    setAlunoId = (event) => {
        this.setState({
            id_aluno: event.target.value
        })
    };

    receber = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'

        const requestOptions = {
            method: 'GET',
            headers: { 'Content-type': 'application/json'},            
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data)
            })
    }

    setClasseSelecionada = (event) => {
        this.setState({
            classe_selecionada: event.target.value
        })
    }

    setNota = (event) => {
        this.setState({
            nota: event.target.value
        })
    }

    setFalta = (event) => {
        this.setState({
            falta: event.target.value
        })
    }

    getTableData = () => {
        const myUrl = 'http://localhost:8000/api/informacaoclasse/3_Ano'
        const requestOptions = {
            method: 'GET',
            headers: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data =>{
                console.log("Chegou dado!!");
                /* data.map(dados => { */
                /*     this.state.tabela.concat(dados.nome_escola) */
                /* }) */
                this.setState({tabela: data})
                /* console.log(this.state.Tabela) */
            })
    }

    getClassInformation = () => {
        const myUrl = 'http://localhost:8000/api/listaclasse/2'
        const requestOptions = {
            method: 'GET',
            headers: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!!");
                this.setState({classe: data})
            })
            console.log(this.state.classe)
    }

    enviar = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'
        let jsonData = {'nome': this.state.nome, 'login': this.state.login, 'senha': this.state.senha}

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-type': 'application/json'},
            body: JSON.stringify(jsonData)
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);

            })
    }

    render(){

        return(
            <div className="TeacherScreen">
                <button onClick={this.getClassInformation}>Get class information</button>
                <button onClick={this.getTableData}>Get table information</button>
                {console.log(this.state.classe)}
                {/* <Conteudo enviar = {this.enviar} 
                          receber = {this.receber} 
                          tableData = {this.getTableData} 
                          tabela = {this.state.tabela} 
                          set_classe_selecionada = {this.setClasseSelecionada}
                          setAlunoNome = {this.setAlunoNome} 
                          set_aluno_id   = {this.setAlunoId}
                          id = {this.state.id_aluno}
                          classInformation = {this.getClassInformation}
                          classe = {this.state.classe}
                          set_nota = {this.setNota}
                          set_falta = {this.setFalta}
                          teste = {this.state.classe_selecionada}/> */}
                <Main2 setClasseSelecionada = {setClasseSelecionada}/>
            </div>
        )
    }
}
