import React from "react";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import "bootstrap/dist/css/bootstrap.min.css";
import Botao from './Botao';


export default props => {
  /* const lista_classe = props.classe.map(dados => {
        return(
          <option key = {dados.Classe} value={dados.Classe}>{dados.Classe}</option>
        )
  }) */
  const lista_todos = props.tabela.map(dados => {
        return (
          <tr>
            <th scope="row">{dados.id}</th>
            <td>{dados.nome}</td>
            <td>{dados.notas}</td>
            <td>{dados.faltas}</td>
          </tr>

        )
    })
    return(
        <div>
            <button type="button" class="btn btn-outline-success" onClick = {props.enviar}>Enviar para backend</button>
            <button type="button" class="btn btn-outline-success" onClick = {props.receber}>Receber do backend</button>
            {/* <Botao NomeBotao = "Remover"/> */}
            <header className="head1"></header>
            <header className="head2">
              <h2>&emsp; EducaUB</h2>
            </header>
            <div class = "container">


            <h2>Painel do professor</h2>

            {/* Seleção de classe */}
            <select value={props.teste} onChange={props.setClasse, props.getTableData}>
              {/* {lista_classe} */}
          </select>
            <p>O valor selecionado é {props.teste}</p>

            {/* Mostra as informações dos alunos */}
            <table class="table table-striped">
              <thead class="thead-dark">
                  <tr>
                    <th scope="col">Id</th>
                    <th scope="col">Nome</th>
                    <th scope="col">Nota 1</th>
                    <th scope='col'>faltas</th>
                  </tr>
                </thead>
                <tbody>
                  { /* lista_todos */ }
                </tbody>
            </table>
            <button type="button" class="btn btn-outline-success" onClick = {props.setOperacao}>Selecionar</button>



        </div>
        </div>
    )
}
