import React, { useState } from 'react'
import Dropmenu from './Dropmenu'
import ShowTable from './ShowTable'
import GetInfo from './getInfo'
import { Form, Col, Button } from 'react-bootstrap';
/* import { FaWhatsapp, FaMailBulk } from "react-icons/fa"; */

import './styles.css'

export default class TeacherScreen extends React.Component
{
    constructor(props){
        super(props)
        this.state = {
            nota: 0,
            falta: 0,
            id_professor: 1,
            id: 0,
            tabela: [],
            classes: [],
            classe_selecionada: '',
            id_estudante: 0,
            id_disciplina: 0,
            faltas: 0,
            data_inicio: 0,
            data_fim: 0
        };
        this.setClasseSelecionada2 = this.setClasseSelecionada2.bind(this)
    }



    setId = (event) => {
        this.setState({
            id: event.target.value
        })
    };

    setClasseSelecionada2(event){
        this.setState({
            classe_selecionada: event.target.value
        })
    }
    setClasseSelecionada = (event) => {
        this.setState({
            classe_selecionada: event.target.value
        })
        console.log(event.target.value)
        console.log(this.state.classe_selecionada)
        this.getTableData()
    }

    setNota = (event) => {
        this.setState({
            nota: event.target.value
        })
    }

    setFalta = (event) => {
        this.setState({
            faltas: parseInt(this.state.faltas,10) +  parseInt(event.target.value,10)
        })
    }

    getClasseSelecionada = () => {
        return this.state.classe_selecionada
    }

    getTableData = () => {
        console.log(this.classe_selecionada)
        const myUrl = 'http://localhost:8000/api/informacaoclasse/'.concat(this.state.classe_selecionada)
        const requestOptions = {
            method: 'GET',
            headers: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data =>{
                console.log("Chegou dado!!");
                /* data.map(dados => { */
                /*     this.state.tabela.concat(dados.nome_escola) */
                /* }) */
                this.setState({tabela: data})
                /* console.log(this.state.Tabela) */
            })
    }

    getClassInformation = () => {
        const myUrl = 'http://localhost:8000/api/listaclasse/'.concat(this.state.id_professor)
        const requestOptions = {
            method: 'GET',
            headers: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!!");
                this.setState({classes: data})
            })
            console.log(this.state.classes)
    }

    getAlunoInfo = () => {
        const myUrl = 'http://localhost:8000/api/disciplinaestudante/'.concat(this.state.id, "/")
        const requestOptions = {
            method: 'GET',
            headers: {'Content-type': 'application/json'}   
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado novo !!!!!")
                console.log(data)
                this.setState({id_estudante: data.id_estudante})
                this.setState({id_disciplina: data.id_disciplina})
                this.setState({faltas: data.faltas})
                this.setState({data_inicio: data.data_inicio})
                this.setState({data_fim: data.data_fim})
            })
            this.atualizarAluno()

    }

    atualizarAluno = () => {
        const myUrl = 'http://localhost:8000/api/disciplinaestudante/'.concat(this.state.id, "/")
        let jsonData = {"id_estudante": this.state.id_estudante,
                        "id_disciplina": this.state.id_disciplina,
                        "notas": this.state.nota,
                        "faltas": this.state.faltas,
                        "data_inicio": this.state.data_inicio,
                        "data_fim": this.state.data_fim}

        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-type': 'application/json'},
            body: JSON.stringify(jsonData)
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);

            })
    }

    enviar = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'
        let jsonData = {'nome': this.state.nome, 'login': this.state.login, 'senha': this.state.senha}

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-type': 'application/json'},
            body: JSON.stringify(jsonData)
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);

            })
    }

    receber = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'

        const requestOptions = {
            method: 'GET',
            headers: { 'Content-type': 'application/json'},            
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data)
            })
    }

    render(){

        return(
            <div className="TeacherScreen">
                <button onClick={this.getClassInformation}>Get class information</button>
                    <header className="head1"></header>
                    <header className="head2">
                        <h2>&emsp; EducaUB</h2>
                    </header>
                    <div className="bg" />
                    <div className="bg bg2" />
                    <div className="bg bg3" />
                    <div class = "container">
                        <h2>Painel do professor</h2>


                        <Dropmenu setClasseSelecionada = {this.setClasseSelecionada} classe_seleciona = {this.state.classe_selecionada} classes = {this.state.classes}/>
                        {/* <Form style={{ margin: '5px' }}>
                          <Form.Label>Estabelecimento</Form.Label>
                              <Form.Control as="select" defaultValue="Selecione..." value={this.state.classe_selecionada} onChange={this.setClasseSelecionada2}>
                                  <option>01 - CNPJ</option>
                                  <option>3 Ano</option>
                                  <option>03 - CAEPF</option>
                          </Form.Control>
                        </Form> */}
                        <ShowTable tabela = {this.state.tabela} />

                        <h2>Notas e faltas</h2>
                        <GetInfo texto = "Id do aluno"   inputText = {this.state.id} set_valor   = {this.setId}/>
                        <GetInfo texto = "Nota do aluno" inputText = {this.state.nota} set_valor = {this.setNota}/>
                        <br />
                        <select onChange={this.setFalta}>
                            <option value = '' class = ""></option>
                            <option value = '0' class = "">Presente</option>
                            <option value = '1' class = "">Não presente</option>
                        </select>
                        <br/>
                        <br/>
                        <button type="button" class="btn btn-primary" onClick = {this.atualizarAluno} >Enviar informações</button>

                    </div>
                    <footer>
                        <div>
                            <h3>
                                &emsp; Dúvidas? Contate-nos: &emsp;{" "}

                                    {" "}
                                    {/* <FaMailBulk /> */} xxxxx.xxxxx@ufu.br &emsp; {/* <FaWhatsapp /> */} (34)
                                    9xxxx-xxxx{" "}

                            </h3>
                        </div>
                    </footer>
            </div>
        )
    }
}
