import React, { useState } from 'react'
import Cabecalho from './Cabecalho'
import Conteudo from './Conteudo'

import './styles.css'

export default class TeacherScreen extends React.Component
{
    constructor(props){
        super(props)
    }

    state = {
        nome: '',
        id_aluno: 0,
        tabela: [],
        operacao: 10
    };

    setOperacao = () => {
        this.setState({
            operacao: 20
        })
    }

    setAlunoNome = (novaClasse) => {
        this.setState({
            nome: novaClasse
        })
    };

    setAlunoId = (aluno_id) => {
        this.setState({
            id_aluno: aluno_id
        })
    };

    receber = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'

        const requestOptions = {
            method: 'GET',
            headers: { 'Content-type': 'application/json'},            
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data)
            })
    }

    getTableData = () => {
        const myUrl = 'http://localhost:8000/api/like/'
        const requestOptions = {
            method: 'GET',
            headres: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data =>{
                console.log("Chegou dado!!");
                /* data.map(dados => { */
                /*     this.state.tabela.concat(dados.nome_escola) */
                /* }) */
                this.setState({tabela: data})
                /* console.log(this.state.Tabela) */
            })
    }

    enviar = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'
        let jsonData = {'nome': this.state.nome, 'login': this.state.login, 'senha': this.state.senha}

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-type': 'application/json'},
            body: JSON.stringify(jsonData)
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);

            })
    }

    render(){

        return(
            <div className="TeacherScreen">
                <Conteudo enviar = {this.enviar} 
                          receber = {this.receber} 
                          tableData = {this.getTableData} 
                          tabela = {this.state.tabela} 
                          setAlunoNome = {this.state.setAlunoNome} 
                          set_aluno_id   = {this.state.setAlunoId}
                          setOperacao = {this.state.setOperacao}
                          operacao = {this.state.operacao}
                          id = {this.state.id_aluno}/>


            </div>
        )
    }
}
