import React, { useState } from 'react'
import Cabecalho from './Cabecalho'
import Conteudo from './Conteudo'

import './styles.css'

export default class TeacherScreen extends React.Component
{
    constructor(props){
        super(props)
    }

    state = {
        /* nomeProfessor: '', */
        /* classe: '' */
        nome: 'Luciano',
        login: 'Luciano',
        senha: 'Teste123',
        tabela: [
            {
              "id": 1,
              "nome_escola": "Escola Estadual João Rezende",
              "estado": "MG",
              "cidade": "Uberlândia",
              "bairro": "Custôdio Pereira",
              "rua": "Teste"
            },
            {
              "id": 2,
              "nome_escola": "Escola Estadual Custódio da Costa Pereira",
              "estado": "MG",
              "cidade": "Uberlândia",
              "bairro": "Custôdio Pereira",
              "rua": "Teste"
            }
          ]
    };

    setClasse = (novaClasse) => {
        this.setState({
            classe: novaClasse
        })
    }

    setTabela = (dados) =>{
        this.setState({
            Tabela: dados
        })
    }

    receber = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'

        const requestOptions = {
            method: 'GET',
            headers: { 'Content-type': 'application/json'},            
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data)
            })
    }

    getTableData = () => {
        const myUrl = 'http://localhost:8000/api/escola/'
        const requestOptions = {
            method: 'GET',
            headres: {'Content-type': 'application/json'},
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data =>{
                console.log("Chegou dado!!");
                /* data.map(dados => { */
                /*     this.state.tabela.concat(dados.nome_escola) */
                /* }) */
                console.log(data)
                console.log(this.state.tabela)
                /* console.log(this.state.Tabela) */
            })
    }

    enviar = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'
        let jsonData = {'nome': this.state.nome, 'login': this.state.login, 'senha': this.state.senha}

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-type': 'application/json'},
            body: JSON.stringify(jsonData)
        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);

            })
    }

    render(){
        const lista_todos = notas.map(nota => 
            {
                return (
                    <ul>
                        <li>
                            <strong>nome:</strong> {nota.nome} <br/>
                            <strong>nota 1:</strong> {nota.nota_1} <br/>
                            <strong>nota 2:</strong> {nota.nota_2} <br/>
                            <strong>nota 3:</strong> {nota.nota_3} <br/>
                            <strong>nota 4:</strong> {nota.nota_4} <br/>
                            <strong>status: </strong> { calcula_media(nota.nota_1, nota.nota_2, nota.nota_3, nota.nota_4)}
                        </li>
                    </ul>
                )
            })
        return(
            <div className="TeacherScreen">
                <Cabecalho nome = "Luciano"/>
                <Conteudo enviar = {this.enviar} receber = {this.receber} tableData = {this.getTableData} data = {this.state.tabela}/>
                {/* <Teste classe = {this.state.classe} setClasse = {this.setClasse} /> */}
                {/* {this.state.tabela.map(dados=> {
                    <p>dados.nome_escola</p>
                })} */}
                {lista_todos}

            </div>
        )
    }
}
