import React from 'react'
import Cabecalho from './Cabecalho'
import Conteudo from './Conteudo'

import './styles.css'


export default class TeacherScreen extends React.Component
{
    constructor(props){
        super(props)
    }

    state = {
        nomeProfessor: '',
        classe: '',
        dados: ''
    };

    setClasse = (novaClasse) => {
        this.setState({
            classe: novaClasse
        })
    }

    enviar = () => {
        const myUrl = 'http://localhost:8000/api/pessoa/'
        let jsonData = {'nome': 'Keroly', 'login': 'Keroly', 'senha': 'Teste123'}

        const requestOptions = {
            method: 'GET',
            headers: { 'Content-type': 'application/json'},

        };
        fetch(myUrl, requestOptions)
            .then(response => response.json())
            .then(data => {
                console.log("Chegou dado!!");
                console.log(data);
                this.dados = data
            })
    }

    render(){
        return(
            <div className="TeacherScreen">
                <Cabecalho nome = "Luciano"/>
                <Conteudo classe = {this.state.classe} setClasse = {this.setClasse} enviar = {this.enviar}/>
                {/* <Teste classe = {this.state.classe} setClasse = {this.setClasse} /> */}

            </div>
        )
    }
}
