import React from 'react'
import Botoes from './BotoesCalc'
import Display from './DisplayCalc'

export default class Calculadora extends React.Component
{
    constructor(props) {
        super(props)
    }

    state = {
        numero1:  '',
        numero2:  '',
        operacao: '',
        resultado: ''
    };

    incNum = (id1) => {
        if (this.state.operacao === ''){
            this.setState({
                numero1: this.state.numero1 * 10 + id1,
                resultado: ''
            })
        }

        else{
            this.setState({
                numero2: this.state.numero2 * 10 + id1
            })
        }
    }

    setOperacao = (id) => {
        this.setState({
            operacao: id
        })
    }

    setResultado = () => {
        if (this.state.operacao === '+'){
            this.setState({
                resultado: this.state.numero1 + this.state.numero2,
                numero1: '',
                numero2: '',
                operacao: ''
            })
        }
        else if (this.state.operacao === '-'){
            this.setState({
                resultado: this.state.numero1 - this.state.numero2,
                numero1: 0,
                numero2: 0,
                operacao: ''
            })
        }

        else if (this.state.operacao === '*'){
            this.setState({
                resultado: this.state.numero1 * this.state.numero2,
                numero1: 0,
                numero2: 0,
                operacao: ''
            })
        }

        else if (this.state.operacao === '/'){
            this.setState({
                resultado: this.state.numero1 / this.state.numero2,
                numero1: 0,
                numero2: 0,
                operacao: ''
            })
        }

    }


    render(){
            return(
            <div className="Calculadora">
                <p>Teste teste</p>
                <p>Teste teste</p>
            </div>
        )}
    }
